    <div className='wrapper'>
      <Link to={"/"}></Link>
      {currentUser ? (
        <Layout logOut={logOut} showAdminBoard={showAdminBoard}>
          <Switch>
            <Route exact path={["/", "/home"]}>
              <Home/>
            </Route>
            <Route exact path="/profile">
              <Profile/>
            </Route>
            <Route path="/user">
              <BoardUser/>
            </Route>
            <Route path="/admin">
              <BoardAdmin/>
            </Route>
            <Route path="/banner/manage">
              <BannerManage/>
            </Route>
            <Route path="/banner/create" >
              <CreateBanner showAdminBoard={showAdminBoard}/>
            </Route>
            <Route path="/banner/update/:code">
              <UpdateBanner/>
            </Route>
            <Route path="/banner/update">
              <UpdateBanner/>
            </Route>
            <Route path="/banner/delete">
              <DisplayBanner/>
            </Route>
            <Route path="/report">
              <Contact/>
            </Route>
          </Switch>
        </Layout>
      ):(
        <Route exact path={["/", "/login"]}>
          <Login/>
        </Route>
      )} 

